class Dep {
    constructor() {
        // 
        this.subs = [];
    }
    // 
    addSub(sub) {
        // update
        if (sub && sub.update) {
            this.subs.push(sub);
        } 
    }
    // update
    notify() {
        this.subs.forEach(sub => {
            sub.update();
        })
    }
}
